$(document).ready(function(){
	$('#nav').affix({
      offset: {
        top: $('header').height()+1
      }
});
	$('#sidebar').affix({
      offset: {
        top: 150
      }
});
	$('#nav').on('affix.bs.affix affix-top.bs.affix', function (e) {
    var padding = e.type === 'affix' ? $(this).height() : '';
    $('body').css('padding-top', padding);
});
});

